import React, { useState } from 'react';

const Form = ({ onSubmit }) => {
    const [formData, setFormData] = useState({
        Temperature: '',
        Humidity: '',
        Pressure: '',
        LogDistance: '',
        WindSpeed: '',
        Precipitation: '',
        SevereWeather: '',
        Latitude: '',
        Longitude: '',
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="p-4 bg-white shadow-lg rounded border border-light">
            <h3 className="text-center mb-4 text-primary">Predict Accident Severity</h3>

            {/* Loop through all fields except Latitude and Longitude */}
            {Object.keys(formData).map((field) => {
                if (field === 'Latitude' || field === 'Longitude') {
                    return null; // Skip Latitude and Longitude in this loop
                }

                return (
                    <div key={field} className="form-group">
                        <label className="form-label">{field.replace(/([A-Z])/g, ' $1')}:</label>
                        <input
                            type="text"
                            name={field}
                            value={formData[field]}
                            onChange={handleChange}
                            className="form-control"
                            placeholder={`Enter ${field.replace(/([A-Z])/g, ' $1')}`}
                            required
                        />
                    </div>
                );
            })}

            {/* Latitude and Longitude on the same row */}
            <div className="form-row">
                <div className="form-group">
                    <label className="form-label">Latitude:</label>
                    <input
                        type="text"
                        name="Latitude"
                        value={formData.Latitude}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Enter Latitude"
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="form-label">Longitude:</label>
                    <input
                        type="text"
                        name="Longitude"
                        value={formData.Longitude}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Enter Longitude"
                        required
                    />
                </div>
            </div>

            <button type="submit" className="btn btn-primary btn-lg w-100">
                Predict
            </button>
        </form>
    );
};

export default Form;
