import React, { useState } from 'react';

const Form = ({ onSubmit }) => {
    const [formData, setFormData] = useState({
        Temperature: '',
        Humidity: '',
        Pressure: '',
        LogDistance: '',
        WindSpeed: '',
        Precipitation: '',
        SevereWeather: '',
        Latitude: '',
        Longitude: '',
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="p-3 bg-white shadow-lg rounded border border-light">
            <h3 className="text-center mb-3 text-primary">Predict Accident Severity</h3>

            <div className="section-header">Weather Data</div>
            <div className="divider"></div>
            {['Temperature', 'Humidity', 'Pressure'].map((field) => (
                <div key={field} className="form-group">
                    <label className="form-label">{field}:</label>
                    <input
                        type="text"
                        name={field}
                        value={formData[field]}
                        onChange={handleChange}
                        className="form-control"
                        placeholder={`Enter ${field} (e.g., ${field === 'Temperature' ? '25°C' : ''})`}
                        required
                    />
                </div>
            ))}

            <div className="section-header">Location Data</div>
            <div className="divider"></div>
            <div className="form-row">
                <div className="form-group">
                    <label className="form-label">Latitude:</label>
                    <input
                        type="text"
                        name="Latitude"
                        value={formData.Latitude}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Enter Latitude"
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="form-label">Longitude:</label>
                    <input
                        type="text"
                        name="Longitude"
                        value={formData.Longitude}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Enter Longitude"
                        required
                    />
                </div>
            </div>

            <button type="submit" className="btn btn-primary btn-lg w-100">
                Predict
            </button>
        </form>
    );
};

export default Form;
